import React, { useState, useNavigate } from "react"
import styled from "styled-components"
import { Link  } from "react-router-dom"
export default function ForgotPassWord(){

  const handleOnChange = (e) => {
    setValues({...values, [e.target.name]: e.target.value})
  }

  const [values, setValues] = useState({
    phone: "",
    password: "",
    confirmPassword: ""
  })



    return (
      <>
          <FormContainer>

                <form>
                    <div className="brand">
                        <h1>Quên mật khẩu</h1>
                    </div>
                    <div className="phone-input">
                        <p>+84</p>
                        <input type="text" placeholder="Phone number" name="phone" onChange={(e) => handleOnChange(e)} />
                    </div>
                    <div id="recaptcha-container"></div>
                    <button type="submit">Send OTP</button>
                    <span>Already have an account <Link to="/login">Login</Link></span>
                </form>


                <form>
                    <div className="brand">

                        <h1> Xác thực OTP</h1>
                    </div>
                    <input type="text" placeholder={"Enter your OTP"} />
                    <div id="recaptcha-container"></div>
                    <button type="submit">Verify</button>
                    <span>Already have an account <Link to="/login">Login</Link></span>
                </form>


                <form>
                    <div className="brand">

                        <h1>Đổi mật khẩu</h1>
                    </div>
                    <input type="password" placeholder="Password" name="password" onChange={(e) => handleOnChange(e)} />
                    <input type="password" placeholder="Confirm Password" name="confirmPassword" onChange={(e) => handleOnChange(e)} />
                    <button type="submit">Change password</button>
                    <span>Already have an account <Link to="/login">Login</Link></span>
                </form>

        </FormContainer>

      </>
    )

}
const FormContainer = styled.div`
    height: 100vh;
    width: 100vw;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    gap: 1rem;
    background-color: #e8f3ff;
    .brand {
        display: flex;
        align-items: center;
        gap: 1rem;
        justify-content: center;
        img {
            height: 5rem;
        }
        h1 {
            /* color: white; */
            text-transform: uppercase
        }
    }
    .phone-input {
        display: flex;
        align-items: center;
        justify-content: center;
        gap: 1rem;
        p {
            height: 80%;
            display: flex;
            align-items: center;
            justify-content: center;
            background-color: #fff;
            padding: 0.5rem;
        }
    }
    form {
        display: flex;
        flex-direction: column;
        gap: 2rem;
        background-color: #fff;
        box-shadow: 0 8px 24px rgb(21 48 142 / 14%);
        border-radius: 2rem;
        padding: 3rem 5rem; 
    }
    input {
        background-color: transparent;
        padding: 1rem;
        border: 0.1rem solid #ccc;
        border-radius: 0.4rem;
        /* color: white; */
        width: 100%;
        font-size: 1rem;
        &:focus {
            /* border: 0.1rem solid #997af0; */
            outline: none;
        }
    }
    select {
        background-color: transparent;
        padding: 1rem;
        border: 0.1rem solid #4e0eff;
        border-radius: 0.4rem;
        color: white;
        width: 100%;
        font-size: 1rem;
        &:focus {
            border: 0.1rem solid #997af0;
            outline: none;
        }
        option {
            background-color: transparent;
            padding: 1rem;
            border: 0.1rem solid #4e0eff;
            border-radius: 0.4rem;
            color: black;
            width: 100%;
            font-size: 1rem;
            &:focus {
                border: 0.1rem solid #997af0;
                outline: none;
            }
        }
    }
    button {
        background-color: #0068ff;
        padding: 1rem 2rem;
        color: white;
        cursor: pointer;
        border: none;
        font-weight: bold;
        border-radius: 0.4rem;
        font-size: 1rem;
        text-transform: uppercase;
        transition: 0.5s ease-in-out;
        &:hover {
            background-color: #0184e0;
        }
    }
    span {
        /* color: white; */
        text-transform: uppercase;
        a {
            text-decoration: none;
            font-weight: bold;
            color: #0068ff;
        }
    }
`;
