import React, { useState } from "react"
import { Link, useNavigate } from "react-router-dom";
import styled from 'styled-components'

function Login(){

    const navigate = useNavigate();

    const [values,setValues] = useState({
        phone: "",
        password: "",
    })

    const handleOnChange = (e)=> {
        setValues({...values, [e.target.name]: e.target.value})

    }

    return(
     <>
        <FormContainer>
            <form >
                <h1>Đăng nhập</h1>
                <input type="text" placeholder="Phone" name="phone" onChange={(e) => handleOnChange(e)}/>
                <input type="password" placeholder="Password" name="password" onChange={(e) => handleOnChange(e)} />
                <button type="submit">Login In</button>
                <span><Link to="/forgotPassword">Forgot password ?</Link></span>
                <span>Already have an account <Link to="/register">Register</Link></span>
            </form>
        </FormContainer>
     </>
    )
}

const FormContainer = styled.div`
height: 100vh;
width: 100vw;
display: flex;
flex-direction: column;
justify-content: center;
align-items: center;
gap: 1rem;
background-color: #e8f3ff;
.brand {
    display: flex;
    align-items: center;
    gap: 1rem;
    justify-content: center;
    img {
        height: 5rem;
    }
    h1 {
        /* color: white; */
        text-transform: uppercase
    }
}
form {
    display: flex;
    flex-direction: column;
    gap: 2rem;
    background-color: #fff;
    border-radius: 2rem;
    padding: 3rem 5rem; 
    box-shadow: 0 8px 24px rgb(21 48 142 / 14%);
}
input {
    background-color: transparent;
    padding: 1rem;
    border: 0.1rem solid #ccc;
    border-radius: 0.4rem;
    /* color: white; */
    width: 100%;
    font-size: 1rem;
    &:focus {
        /* border: 0.1rem solid #ccc; */
        outline: none;
    }
}
button {
    background-color: #0068ff;
    padding: 1rem 2rem;
    color: white;
    cursor: pointer;
    border: none;
    font-weight: bold;
    border-radius: 0.4rem;
    font-size: 1rem;
    text-transform: uppercase;
    transition: 0.5s ease-in-out;
    &:hover {
        background-color: #0184e0;
    }
}
span {
    /* color: white; */
    text-transform: uppercase;
    a {
        text-decoration: none;
        font-weight: bold;
        color: #0068ff;
    }
}
`;
export default Login;
