import React, { useState } from "react";
import styled from 'styled-components';
import { Link } from "react-router-dom";
import Logo from "../assets/Logo.png";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import axios from "axios";
import { registerRoute } from "../utils/APIRoutes";

export default function Register() {
    const [values, setValues] = useState({
        username: "",
        email: "",
        password: "",
        confirmPassword: "",
    });


    const handleSubmit = async (e) => {
        e.preventDefault();
        if (handleValidation()) {
            console.log("in validtaion", registerRoute);
            const { password, confirmPassword, username, email } = values;
            const { data } = await axios.post(registerRoute, {
                username,
                email,
                password,
            });
        }
    };

    const handleValidation = () => {
        const { password, confirmPassword, username, email } = values;
        if (password !== confirmPassword) {
            toast.error("password và confirmpassword phải giống nhau.", toastOptions);
            return false;
        }
        if (username.length < 3) {
            toast.error("Username không được ít hơn 3 ký tự.", toastOptions);
            return false;
        }
        else if (email === "") {
            toast.error("Email không được để trống.", toastOptions);
            return false;
        }

        else if (password.length < 3) {
            toast.error("Password không được ít hơn 3 ký tự", toastOptions);
            return false;
        }
        return true;
    };

    const handleOnChange = (e) => {
        setValues({ ...values, [e.target.name]: e.target.value });
    };

    const toastOptions = {
        position: "bottom-right",
        autoClose: 8000,
        draggable: true,
        pauseOnHover: true,
        theme: "dark"
    };
    return (
        <>
            <FormContainer>

                <form onSubmit={(e) => handleSubmit(e)}>
                    <div className="brand">
                        <img src={Logo} alt="Logo" />
                        <h1>Q&V</h1>
                    </div>
                    <input type="text" placeholder="Username" name="username" onChange={(e) => handleOnChange(e)} />
                    <input type="email" placeholder="Email" name="email" onChange={(e) => handleOnChange(e)} />
                    <input type="password" placeholder="Password" name="password" onChange={(e) => handleOnChange(e)} />
                    <input type="password" placeholder="Confirm Password" name="confirmPassword" onChange={(e) => handleOnChange(e)} />
                    <button type="submit">Create user</button>
                    <span>Already have an account <Link to="/login">Login</Link></span>
                </form>

            </FormContainer>
            <ToastContainer />
        </>
    );
}
const FormContainer = styled.div`
    height: 100vh;
    width: 100vw;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    gap: 1rem;
    background-color: #e8f3ff;
    .brand {
        display: flex;
        align-items: center;
        gap: 1rem;
        justify-content: center;
        img {
            height: 5rem;
        }
        h1 {
            /* color: white; */
            text-transform: uppercase
        }
    }
    .phone-input {
        display: flex;
        align-items: center;
        justify-content: center;
        gap: 1rem;
        p {
            height: 80%;
            display: flex;
            align-items: center;
            justify-content: center;
            background-color: #fff;
            padding: 0.5rem;
        }
    }
    form {
        display: flex;
        flex-direction: column;
        gap: 2rem;
        background-color: #fff;
        border-radius: 2rem;
        padding: 3rem 5rem; 
        box-shadow: 0 8px 24px rgb(21 48 142 / 14%);
    }
    input {
        background-color: transparent;
        padding: 1rem;
        border: 0.1rem solid #ccc;
        border-radius: 0.4rem;
        /* color: white; */
        width: 100%;
        font-size: 1rem;
        &:focus {
            /* border: 0.1rem solid #997af0; */
            outline: none;
        }
    }
    select {
        background-color: transparent;
        padding: 1rem;
        border: 0.1rem solid #ccc;
        border-radius: 0.4rem;
        /* color: white; */
        width: 100%;
        font-size: 1rem;
        &:focus {
            /* border: 0.1rem solid #997af0; */
            outline: none;
        }
        option {
            background-color: transparent;
            padding: 1rem;
            border: 0.1rem solid #ccc;
            border-radius: 0.4rem;
            color: black;
            width: 100%;
            font-size: 1rem;
            &:focus {
                /* border: 0.1rem solid #997af0; */
