        <>
            <FormContainer>
                <form onSubmit={(event) => handleSubmit(event)} >
                    <div className="brand">
                        <img src={Logo} alt="Logo" />
                        <h1>Đăng nhập</h1>
                    </div>
                    <input type="text" placeholder="Phone" name="phone" onChange={(e) => handleOnChange(e)} />
