}

const FormContainer = styled.div`
height: 100vh;
width: 100vw;
display: flex;
flex-direction: column;
justify-content: center;
align-items: center;
gap: 1rem;
background-color: #e8f3ff;
.brand {
    display: flex;
    align-items: center;
    gap: 1rem;
    justify-content: center;
    img {
        height: 5rem;
    }
    h1 {
        /* color: white; */
        text-transform: uppercase
    }
}
form {
    display: flex;
    flex-direction: column;
    gap: 2rem;
    background-color: #fff;
    border-radius: 2rem;
    padding: 3rem 5rem; 
    box-shadow: 0 8px 24px rgb(21 48 142 / 14%);
}
input {
    background-color: transparent;
    padding: 1rem;
    border: 0.1rem solid #ccc;
    border-radius: 0.4rem;
    /* color: white; */
    width: 100%;
    font-size: 1rem;
    &:focus {
        /* border: 0.1rem solid #ccc; */
        outline: none;
    }
}
button {
    background-color: #0068ff;
    padding: 1rem 2rem;
